<?php/*  Template Name: Blog */?><?php get_header(); ?><div class="page-content">    <div class="grid_sub_16 sub_alpha">        <div class="content-bar">            <?php            $limit = get_option('posts_per_page');            $paged = (get_query_var('paged')) ? get_query_var('paged') : 1;            query_posts('showposts=' . $limit . '&paged=' . $paged);            $wp_query->is_archive = true;            $wp_query->is_home = false;            ?>            <!--Start Loop-->            <!--post start-->            <?php if (have_posts()) : while (have_posts()) : the_post(); ?>                    <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>                        <h1 class="post_title"><a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>"><?php the_title(); ?></a></h1>                        <ul class="post_meta">                            <li class="post_date"><?php the_time(get_option('date_format')); ?></li>                            <li class="post_category">                                <?php the_category(', '); ?>                            </li>                            <li class="posted_by">                                <?php the_author_posts_link(); ?>                            </li>                            <li class="postc_comment"><span></span>&nbsp;                                <?php comments_popup_link('No Comments.', '1 Comment.', '% Comments.'); ?>                            </li>                        </ul>                        <div class="clear"></div>                        <div class="post_content">                            <?php if ((function_exists('has_post_thumbnail')) && (has_post_thumbnail())) { ?>                                <?php elite_get_thumbnail(182, 165); ?>                            <?php } else { ?>                                <?php elite_get_image(182, 165); ?>                                <?php                            }                            ?>                            <div class="post_data">                            <?php the_excerpt(); ?>                            <div class="clear"></div>                            <?php if (has_tag()) { ?>                                <div class="tag">                                    <?php the_tags('Post Tagged with ', ', ', ''); ?>                                </div>                            <?php } ?>                            <a class="read_more" href="<?php the_permalink(); ?>">Read more</a>                            </div>                        </div>                    </div>                    <!--post End-->                <?php endwhile;            else: ?>                <div class="post">                    <p>                        <?php _e('Sorry, no posts matched your criteria.', 'elite'); ?>                    </p>                </div>            <?php endif; ?>            <!--End Loop-->                <nav id="nav-single"> <span class="nav-previous">        <?php next_posts_link( __( '&larr; Older posts', 'elite' ) ); ?>        </span> <span class="nav-next">        <?php previous_posts_link( __( 'Newer posts &rarr;', 'elite' ) ); ?>        </span> </nav>        </div>    </div>    <div class="grid_sub_8 sub_omega">        <?php get_sidebar(); ?>    </div></div></div><?php get_footer(); ?>